import logo from "./logo.svg";
import "./App.css";
import React from "react";
import web3 from "./web3";
import lottery from "./lottery";

class App extends React.Component {
  state = {
    manager: '',
    players:[],
    balance:'',
    value:'',
    message:''
  };

  async componentDidMount(){
      const manager = await lottery.methods.manager().call();
      const players = await lottery.methods.getPlayers().call();
      const balance = await web3.eth.getBalance(lottery.options.address);
      this.setState({manager,players,balance});
  }



  onSubmit = async(event) => {
    event.preventDefault();

    const accounts = await web3.eth.getAccounts();
    this.setState({message: 'waiting in transaction success....'})

    await lottery.methods.enter().send({
      from: accounts[0],
      value: web3.utils.toWei(this.state.value , 'ether')
    })

    this.setState({message: 'you have been entered!'})
  }

  onClick = async() => {
    const accounts = await web3.eth.getAccounts();
    this.setState({message: 'waiting on transaction success....'})

    await lottery.methods.pickWinner().send({
      from: accounts[0]
    })

    this.setState({message: 'A winner has been picked!'});
  }


  render() {

    const myLotteryStyle = {
      color: "white",
      backgroundColor: "DodgerBlue",
      padding: "10px",
      fontFamily: "Arial"
    }
    const infoStyle = {
      paddingLeft: "50px",
      fontFamily: "Arial",
      margin: "40px"
    }

    return (
      <div>
        <center><h2 style={myLotteryStyle}>Lottery Contract</h2></center>
         <p style={infoStyle}>this Contract is managed by {this.state.manager}
         there are currently {this.state.players.length} people entered, competing to win {web3.utils.fromWei(this.state.balance , 'ether')} ether!
         </p>
         <hr />
         <form onSubmit={this.onSubmit}>
          <h4>
            Want to try your luck?
          </h4>
          <div>
            <label>Amount of ether to enter</label>
            <input
            value={this.state.value}
            onChange={event => this.setState({ value: event.target.value})}
            />
          </div>
          <button>Enter</button>
         </form>
         <hr />
          <h4>Ready to pick winner?</h4>
          <button onClick={this.onClick}>Pick a Winner!</button>
         <hr />
         <h1>{this.state.message}</h1>
      </div>
    );
  }
}
export default App;
